// Define a function to validate if a given time falls within the current week's range
function validateWeekRange(time) {
    // Get the current date
    var currentDate = new Date();

    // Get the week number of the current date
    var currentWeek = getWeekNumber(currentDate);

    // Get the week number of the provided time
    var targetWeek = getWeekNumber(time);

    // Check if the time is within the current week
    return currentWeek === targetWeek;
}

// Function to get the week number of a date
function getWeekNumber(date) {
    var d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
    d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay() || 7));
    var yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
    var weekNo = Math.ceil((((d - yearStart) / 86400000) + 1) / 7);
    return weekNo;
}

frappe.ui.form.on('Timesheet Detail', {

    from_time: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        var fromTime = new Date(child.from_time);

        // Check if from_time is within the week's range
        if (!validateWeekRange(fromTime)) {
            frappe.throw("From Date should be within this week");
            frm.set_value(cdt, cdn, 'from_time', '');
        }
    },

    to_time: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        var toTime = new Date(child.to_time);

        // Check if to_time is within the week's range
        if (!validateWeekRange(toTime)) {
            frappe.throw("To Date should be within this week");
            frm.set_value(cdt, cdn, 'to_time', '');
        }
    }
});

frappe.ui.form.on('Timesheet Detail', {
    project: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        console.log(child);
        var project = child.project;
        var from_date_string = child.from_time; // Assuming from_date is in string format
        // Convert from_date string to Date object
        var from_date = new Date(from_date_string);
        // Convert from_date to YYYY-MM-DD format using frappe.datetime
        var formatted_date = frappe.datetime.obj_to_str(from_date).split(' ')[0];
        frappe.call({
            method: 'ctrine.ctrine.server_scripts.story.fetch_hours',
            args:{
                project_name: project,
                from_date: formatted_date,
                employee :frm.doc.employee
                
            },
            callback: function(r) {
                if (r.message) {
                    var totalHours = r.message;
                    frappe.model.set_value(cdt, cdn, 'billing_hours', totalHours);
                }
            }
        });
    }
});

frappe.ui.form.on('Timesheet', {
    refresh: function(frm) {
        if(frappe.user.has_role("Report Manager")){
       if (((cur_frm.doc.workflow_state === 'Approved')||(cur_frm.doc.workflow_state === 'Rejected'))&& !cur_frm.doc.custom_reason) {
            // Define a function to show the dialog
            var showDialog = function() {
                let d = new frappe.ui.Dialog({
                    title: 'Reason',
                    fields: [{
                        label: 'Reason',
                        fieldname: 'reason',
                        fieldtype: 'Small Text'
                    }],
                    size: 'small',
                    primary_action_label: __('Submit'),
                    primary_action: function() {
                        frappe.call({
                            method: 'ctrine.ctrine.server_scripts.reason.reason',
                            args: {
                                docname: frm.docname,
                                reason: d.get_value('reason'),
                            },
                            callback: function(response) {
                                if(response.message==='success'){
                                frappe.show_alert({
                                message: "Reason saved",
                                indicator: "green"
                                });
                                }

                                console.log(response.message);
                                frm.reload_doc();
                                d.hide();
                            }
                        });
                    }
                });
                d.show();
            };

            // Invoke the function to show the dialog
            showDialog();
        }
        } 
    }
});

