script:
frappe.ui.form.on('Project Milestone', {
	refresh(frm) {
	    frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });

    	 frm.add_custom_button(__('Go To Task'), function(){
            window.open(`/app/task?custom_parent_milestone=${frm.doc.name}`)
        });

        frm.add_custom_button(__('Go To Story'), function(){
           window.open(`/app/story?parent_milestone=${frm.doc.name}`)
        });
	}
})

frappe.ui.form.on('Story', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.parent_milestone}`)
        });
        frm.add_custom_button(__('Go To Task'), function(){
            window.open(`/app/task/view/list?custom_parent_story=${frm.doc.name}`)
        });
	}
})

frappe.ui.form.on('Task', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.custom_parent_milestone}`)
        });
        frm.add_custom_button(__('Go To Story'), function(){
            window.open(`/app/story/${frm.doc.custom_parent_story}`)
        });
	}
})

frappe.ui.form.on('Timesheet', {
    onload: function(frm) {
        var creation_datetime;

        // Check if the form is new or being edited
        if (frm.doc.__islocal) {
            // Fetch current date and time when creating a new timesheet
            creation_datetime = frappe.datetime.now_datetime();
        } else {
            // Fetch creation date and time from the parent document
            creation_datetime = frm.doc.creation;
        }

        // Assign date and time for the first row if it exists
        if (frm.doc.time_logs && frm.doc.time_logs.length > 0) {
            frm.doc.time_logs[0].from_time = creation_datetime;
            // Other field assignments can be done similarly
        }

        // Auto-populate rows based on the day of the week
        var dayOfWeek = new Date(creation_datetime).getDay();
        var numRowsToAdd = 0;

        switch(dayOfWeek) {
            case 1: // Monday
                numRowsToAdd = 5;
                break;
            case 2: // Tuesday
                numRowsToAdd = 4;
                break;
            case 3: // Wednesday
                numRowsToAdd = 3;
                break;
            case 4: // Thursday
                numRowsToAdd = 2;
                break;
            case 5: // Friday
                numRowsToAdd = 1;
                break;
                
            default:
                numRowsToAdd = 0; // Add no rows for other days
        }

        // Populate rows in the child table starting from the second row
        var start_datetime = frappe.datetime.add_days(creation_datetime, 1); // Start from the next day
        for (var i = 1; i < numRowsToAdd; i++) { // Start from i=1 for the second row
            var new_row = frappe.model.add_child(frm.doc, "Timesheet Detail", "time_logs");
            new_row.from_time = start_datetime;
            // Other field assignments can be done similarly
            start_datetime = frappe.datetime.add_days(start_datetime, 1); // Move to the next day
        }

        // Refresh the child table to reflect the changes
        frm.refresh_field('time_logs');
    }
});
