script:
frappe.ui.form.on('Timesheet', {
    refresh: function(frm) {
        if(frappe.user.has_role("Report Manager")){
       if (((cur_frm.doc.workflow_state === 'Approved')||(cur_frm.doc.workflow_state === 'Rejected'))&& !cur_frm.doc.reason) {
            // Define a function to show the dialog
            var showDialog = function() {
                let d = new frappe.ui.Dialog({
                    title: 'Reason',
                    fields: [{
                        label: 'Reason',
                        fieldname: 'reason',
                        fieldtype: 'Small Text'
                    }],
                    size: 'large',
                    primary_action_label: __('Submit'),
                    primary_action: function() {
                        frappe.call({
                            method: 'ctrine.ctrine.server_scripts.reason.reason',
                            args: {
                                docname: frm.docname,
                                reason: d.get_value('reason'),
                            },
                            callback: function(response) {
                                if(response.message==='success'){
                                frappe.show_alert({
                                message: "Reason saved",
                                indicator: "green"
                                });
                                }

                                console.log(response.message);
                                frm.reload_doc();
                                d.hide();
                            }
                        });
                    }
                });
                d.show();
            };

            // Invoke the function to show the dialog
            showDialog();
        }
        } 
    }
});

frappe.ui.form.on('Project Milestone', {
	refresh(frm) {
	    frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });

    	 frm.add_custom_button(__('Go To Task'), function(){
            window.open(`/app/task?custom_parent_milestone=${frm.doc.name}`)
        });

        frm.add_custom_button(__('Go To Story'), function(){
           window.open(`/app/story?parent_milestone=${frm.doc.name}`)
        });
	}
})

frappe.ui.form.on('Story', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.parent_milestone}`)
        });
        frm.add_custom_button(__('Go To Task'), function(){
            window.open(`/app/task/view/list?custom_parent_story=${frm.doc.name}`)
        });
	}
})

frappe.ui.form.on('Task', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.custom_parent_milestone}`)
        });
        frm.add_custom_button(__('Go To Story'), function(){
            window.open(`/app/story/${frm.doc.custom_parent_story}`)
        });
	}
})

frappe.ui.form.on('Timesheet', {
    onload: function(frm) {
        // Check if the document is not saved or submitted
        if (frm.doc.__unsaved || frm.doc.docstatus === 0) {
            var creation_datetime;
            // Fetch current date and time when creating a new timesheet
            creation_datetime = frappe.datetime.now_datetime();

            // Calculate the start date of the week (Monday) based on the creation date
            var current_date = new Date(creation_datetime);
            var dayOfWeek = current_date.getDay();
            var start_of_week = new Date(current_date); // Copy current date to avoid modifying the original date
            start_of_week.setDate(current_date.getDate() - dayOfWeek + 1);

            // Clear existing rows in the child table
            frm.clear_table('time_logs');

            // Populate rows in the child table for the days of the week (Monday to Sunday)
            for (var i = 0; i < 7; i++) {
                var new_row = frappe.model.add_child(frm.doc, "Timesheet Detail", "time_logs");
                new_row.from_time = frappe.datetime.add_days(start_of_week, i);
                // Set other fields editable here
            }

            // Refresh the child table to reflect the changes
            frm.refresh_field('time_logs');
        }
    },
    validate: function(frm) {
        // Validate hours to ensure they do not exceed 24 hours for each day
        frm.doc.time_logs.forEach(function(row) {
            if (row.hours > 24) {
                frappe.msgprint("Hours for a day cannot exceed 24 hours.");
                frappe.validated = false;
                return false; // Stop further validation if any row exceeds 24 hours
            }
        });
    }
});

frappe.ui.form.on('Timesheet', {
    refresh: function(frm) {
        if (cur_frm.doc.workflow_state === 'Approval') {
            // Define a function to show the dialog
            var showDialog = function() {
                let d = new frappe.ui.Dialog({
                    title: 'Reason',
                    fields: [{
                        label: 'Reason',
                        fieldname: 'reason',
                        fieldtype: 'Small Text'
                    }],
                    size: 'large',
                    primary_action_label: __('Submit'),
                    primary_action: function() {
                        frappe.call({
                            method: 'ctrine.ctrine.server_scripts.timesheet.approve_reason',
                            args: {
                                docname: frm.docname,
                                reason: d.get_value('reason'),
                            },
                            callback: function(response) {
                                console.log(response.message);
                                frm.reload_doc();
                                d.hide();
                            }
                        });
                    }
                });
                d.show();
            };

            // Invoke the function to show the dialog
            showDialog();
        }
    }
});


