frappe.ui.form.on('Project Milestone', {
	refresh(frm) {
	    frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });

    	 frm.add_custom_button(__('Go To Task'), function(){
            window.open(`/app/task?custom_parent_milestone=${frm.doc.name}`)
        });

        frm.add_custom_button(__('Go To Story'), function(){
           window.open(`/app/story?parent_milestone=${frm.doc.name}`)
        });
	}
})

frappe.ui.form.on('Story', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.parent_project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.parent_milestone}`)
