frappe.ui.form.on('Task', {
	refresh(frm) {
		frm.add_custom_button(__('Go To Project'), function(){
            window.open(`/app/project/${frm.doc.project}`)
        });
        frm.add_custom_button(__('Go To Milestone'), function(){
            window.open(`/app/project-milestone/${frm.doc.custom_parent_milestone}`)
        });
        frm.add_custom_button(__('Go To Story'), function(){
            window.open(`/app/story/${frm.doc.custom_parent_story}`)
        });
	}
})

frappe.ui.form.on('Timesheet', {
    onload: function(frm) {
        var creation_datetime;

        // Check if the form is new or being edited
        if (frm.doc.__islocal) {
            // Fetch current date and time when creating a new timesheet
            creation_datetime = frappe.datetime.now_datetime();
        } else {
            // Fetch creation date and time from the parent document
            creation_datetime = frm.doc.creation;
        }

        // Calculate the start date of the week (Monday) based on the creation date
        var current_date = new Date(creation_datetime);
        var dayOfWeek = current_date.getDay();
        var start_of_week = new Date(current_date); // Copy current date to avoid modifying the original date
        start_of_week.setDate(current_date.getDate() - dayOfWeek + 1);

        // Clear existing rows in the child table
        frm.clear_table('time_logs');

        // Populate rows in the child table for the days of the week (Monday to Sunday)
        for (var i = 0; i < 7; i++) {
            var new_row = frappe.model.add_child(frm.doc, "Timesheet Detail", "time_logs");
            new_row.from_time = frappe.datetime.add_days(start_of_week, i);
            // Set other fields editable here
        }

        // Refresh the child table to reflect the changes
        frm.refresh_field('time_logs');
    }
});

frappe.ui.form.on('Timesheet', {
    refresh: function(frm) {
        if (frm.doc.workflow_state === 'Approved') {
            // Define a function to show the dialog
            var showDialog = function() {
                let d = new frappe.ui.Dialog({
                    title: 'Reason',
                    fields: [{
                        label: 'Reason',
                        fieldname: 'reason',
                        fieldtype: 'Small Text'
                    }],
                    size: 'large',
                    primary_action_label: __('Submit'),
                    primary_action: function() {
                        frappe.call({
                            method: 'ctrine.ctrine.server_scripts.reasonmsg.approve_reason',
                            args: {
                                docname: frm.docname,
                                reason: d.get_value('reason'),
                            },
                            callback: function(response) {
                                console.log(response.message);
                                frm.reload_doc();
                                d.hide();
                            }
                        });
                    }
                });
                d.show();
            };

            // Invoke the function to show the dialog
            showDialog();
        }
    }
});


